function parseMessage(body) {
  let phone, message, messageType, timestamp, provider, mediaUrl;

  if (body.entry?.[0]?.changes?.[0]?.value?.messages) {
    const msg = body.entry[0].changes[0].value.messages[0];
    phone = msg.from;
    messageType = msg.type;
    message = msg.text?.body || msg.voice?.id || msg.image?.id || `[${messageType}]`;
    mediaUrl = msg.image?.url || msg.voice?.url || msg.document?.url;
    timestamp = msg.timestamp;
    provider = 'meta';
  } else if (body.device && body.message) {
    phone = body.device;
    message = body.message;
    messageType = body.message_type || 'text';
    mediaUrl = body.media_url;
    timestamp = body.date;
    provider = 'fonnte';
  } else {
    return { success: false, error: 'Unsupported format' };
  }

  return { phone, message, messageType, timestamp, provider, mediaUrl, success: true };
}

function securityCheck(phone, message) {
  if (SECURITY.maintenance_mode && !SECURITY.admin_numbers.includes(phone)) {
    return { blocked: true, reason: 'maintenance' };
  }

  if (SECURITY.blocked_numbers.includes(phone)) {
    return { blocked: true, reason: 'blocked' };
  }

  const hasSpam = SECURITY.spam_keywords.some(keyword => 
    message.toLowerCase().includes(keyword)
  );

  if (hasSpam) {
    return { blocked: true, reason: 'spam' };
  }

  return { blocked: false };
}

