const data = $input.all()[0].json;

function detectLanguage(text) {
  const patterns = {
    indonesian: /\b(dan|atau|dengan|untuk|dari|ke|di|pada|yang|adalah|akan|telah|sudah|bisa|dapat|harus|ingin|mau|tidak|bukan|juga|hanya|lebih|sama|lain|semua|setiap|beberapa|banyak|sedikit)\b/i,
    arabic: /[\u0600-\u06FF]/,
    english: /\b(the|and|or|with|for|from|to|in|on|that|is|are|was|were|will|have|has|had|can|could|should|would|want|like|not|also|only|more|same|other|all|every|some|many|few)\b/i,
    javanese: /\b(lan|utawa|karo|kanggo|saka|menyang|ing|ana|sing|bakal|wis|bisa|kudu|pengin|ora|uga|mung|luwih|padha|liya|kabeh|saben|sawetara|akeh|sithik)\b/i
  };

  const scores = {};

  for (const [lang, pattern] of Object.entries(patterns)) {
    const matches = text.match(pattern);
    scores[lang] = matches ? matches.length : 0;
  }

  const detectedLang = Object.keys(scores).reduce((a, b) => 
    scores[a] > scores[b] ? a : b
  );

  return {
    detected: detectedLang,
    confidence: scores[detectedLang] / (text.split(' ').length || 1),
    scores
  };
}

