// Intent Classification Function
function classifyIntent(message) {
  const msg = message.toLowerCase();

  const intents = {
    jadwal_shalat: ['jadwal shalat', 'waktu shalat', 'adzan', 'subuh', 'dzuhur', 'ashar', 'magrib', 'isya'],
    kajian_info: ['kajian', 'pengajian', 'ceramah', 'ustadz', 'kuliah agama'],
    donasi_infaq: ['donasi', 'infaq', 'sedekah', 'zakat', 'transfer', 'rekening', 'qris'],
    acara_kegiatan: ['acara', 'event', 'kegiatan', 'agenda', 'program'],
    informasi_umum: ['info', 'alamat', 'kontak', 'lokasi', 'jam buka', 'fasilitas'],
    daftar_nikah: ['nikah', 'menikah', 'akad', 'pernikahan', 'syarat nikah'],
    konsultasi_agama: ['konsultasi', 'tanya', 'bantuan', 'bimbingan', 'nasihat'],
    salam_greeting: ['assalamualaikum', 'salam', 'halo', 'hai', 'selamat'],
    ai_chat: ['chat', 'tanya', 'bagaimana', 'kenapa', 'apa itu', 'jelaskan']
  };

  for (const [intent, keywords] of Object.entries(intents)) {
    if (keywords.some(keyword => msg.includes(keyword))) {
      return intent;
    }
  }

  return 'ai_chat';
}

