// AI Chat Function using OpenRouter
async function generateAIResponse(message, userPhone) {
  try {
    const systemPrompt = `Anda adalah AI Assistant untuk ${masjidInfo.nama}. Berikan respons yang membantu, islami, dan sopan. Gunakan bahasa Indonesia yang mudah dipahami. Jika ditanya tentang Islam, berikan jawaban berdasarkan Al-Quran dan Hadits shahih. Selalu rujuk ke ustadz untuk pertanyaan fiqh yang kompleks.\n\nInformasi Masjid:\n- Nama: ${masjidInfo.nama}\n- Alamat: ${masjidInfo.alamat}\n- Kontak: ${masjidInfo.whatsapp}\n- Website: ${masjidInfo.website}\n\nUstadz yang tersedia:\n${ustadz.map(u => `- ${u.nama} (${u.spesialisasi.join(', ')}) - ${u.jadwal}`).join('\n')}`;

    const response = await fetch('https://openrouter.ai/api/v1/chat/completions', {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${openRouterApiKey}`,
        'Content-Type': 'application/json',
        'HTTP-Referer': 'https://masjid-ai.com',
        'X-Title': 'Masjid AI Assistant'
      },
      body: JSON.stringify({
        model: 'moonshotai/kimi-k2:free',
        messages: [
          { role: 'system', content: systemPrompt },
          { role: 'user', content: message }
        ],
        max_tokens: 1000,
        temperature: 0.7
      })
    });

    if (response.ok) {
      const data = await response.json();
      let aiResponse = data.choices[0].message.content;

      if (!aiResponse.includes('ü§≤') && !aiResponse.includes('Assalam')) {
        aiResponse = 'ü§≤ ' + aiResponse;
      }

      aiResponse += '\n\nüí° Ketik "menu" untuk layanan lain atau chat langsung untuk pertanyaan!';

      return aiResponse;
    } else {
      return 'Maaf, saya sedang mengalami gangguan. Silakan coba lagi dalam beberapa saat atau hubungi admin di ' + masjidInfo.whatsapp;
    }
  } catch (error) {
    return 'Maaf, terjadi kesalahan teknis. Untuk pertanyaan mendesak, silakan hubungi ' + masjidInfo.whatsapp;
  }
}

// Main Processing Logic
const startTime = Date.now();
const body = $input.all()[0].body;

// Extract message data (support multiple WhatsApp providers)
let phone, message, messageType;

// Fonnte format
if (body.device && body.message) {
  phone = body.device;
  message = body.message;
  messageType = body.message_type || 'text';
}
// Woowa format
else if (body.from && body.body) {
  phone = body.from;
  message = body.body;
  messageType = body.type || 'text';
}
// WhatsApp Business API format
else if (body.entry && body.entry[0]) {
  const entry = body.entry[0];
  if (entry.changes && entry.changes[0]) {
    const change = entry.changes[0];
    if (change.value && change.value.messages) {
      const msg = change.value.messages[0];
      phone = msg.from;
      message = msg.text ? msg.text.body : '';
      messageType = msg.type;
    }
  }
}
// Generic format
else if (body.phone && body.message) {
  phone = body.phone;
  message = body.message;
  messageType = body.type || 'text';
}

// Validate input
if (!phone || !message) {
  return [{
    json: {
      error: 'Invalid webhook format',
      phone: phone || 'unknown',
      message: message || 'empty',
      hasError: true,
      errorType: 'validation'
    }
  }];
}

// Clean phone number
phone = phone.replace(/[^0-9+]/g, '');
if (phone.startsWith('0')) {
  phone = '+62' + phone.substring(1);
} else if (phone.startsWith('62')) {
  phone = '+' + phone;
}

// Only process text messages
if (messageType !== 'text') {
  return [{
    json: {
      phone: phone,
      response: 'üì± Saat ini saya hanya bisa memproses pesan teks. Silakan kirim pesan teks untuk mendapat bantuan.\n\nüìû Untuk urgent: ' + masjidInfo.whatsapp,
      intent: 'media_message',
      hasError: false,
      processing_time: Date.now() - startTime
    }
  }];
}

// Classify intent
const intent = classifyIntent(message);
let response = '';

// Generate response based on intent
switch (intent) {
  case 'jadwal_shalat':
    response = generatePrayerResponse();
    break;
  case 'kajian_info':
    response = generateKajianResponse();
    break;
  case 'donasi_infaq':
    response = generateDonationResponse();
    break;
  case 'acara_kegiatan':
    response = generateEventResponse();
    break;
  case 'informasi_umum':
    response = generateInfoResponse();
    break;
  case 'daftar_nikah':
    response = generateMarriageResponse();
    break;
  case 'konsultasi_agama':
    response = generateConsultationResponse();
    break;
  case 'salam_greeting':
    response = generateGreetingResponse();
    break;
  case 'ai_chat':
  default:
    response = await generateAIResponse(message, phone);
    break;
}

// Add signature
response += '\n\n‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\nüïå ' + masjidInfo.nama + '\nüì± AI Assistant v3.0\nü§ñ Melayani dengan ‚ù§Ô∏è 24/7';

// Return formatted response
return [{
  json: {
    phone: phone,
    message: message,
    intent: intent,
    response: response,
    timestamp: new Date().toISOString(),
    processing_time: Date.now() - startTime,
    hasError: false,
    whatsapp_api_key: 'YOUR_WHATSAPP_API_KEY', // Will be replaced in next node
    supabase_url: supabaseUrl,
    supabase_key: supabaseKey
  }
}];
